import React from 'react';

import './sign-in.styles.scss';


class SignIn extends React.Component{
    constructor(props){
        super(props);
        this.state={
            email:'',
            password:''
        }
    }

    onChange=(event)=>{
        const {value,name}=event.target;
        this.setState({[name]:value});
    }

    handleSubmit=(event)=>{
        event.preventDefault();
        this.setState({
            email:'',
            password:''
        })
    }

    render(){
        return(
            <div className='sign-in' >
                <h2>SIGN IN</h2>
                <span>Sign in with your email and password</span>
                <form onSubmit={this.handleSubmit} >
                    <label>Email</label>
                    <input type='email' name='email' value={this.state.email} required onChange={this.onChange} />
                    <label>Password</label>
                    <input type='password' name='password' value={this.state.password} required onChange={this.onChange} />
                </form>
            </div>
        )
    }
}

export default SignIn;
